import {useState} from "react";

export default function ComponenteNumRandom(){
    const [num, setNum] = useState(Math.random() * (7-1) + 1);
    return <div>
        <p>El número aleatorio es: {num}</p>
        <button onClick={ (setNum)=>num()}>Clicka</button>
    </div>;
}
