import {useEffect, useState} from "react";

export default function PokedexV2(){
    const [name, setName] = useState('');
    const [picture, setPicture] = useState('');
    const [weight, setWeight] = useState('');

    let handleSubmit = async e =>{
        e.preventDefault();

        try{
            const url = `https://pokeapi.co/api/v2/pokemon/${name}`;
            const req = await fetch(url);
            const data = await req.json();
            console.log(data);

            setName(data.name);
            setWeight(data.weight);
            setPicture(data.sprites.front_default);

        }catch (err){
            alert('error');
        }
    }

    let handleChange = e =>{
        const {name,value}=e.target;
        name==='pokemonName'?setName(value):alert('error');
    }

    return <>
        <h3>Pokemon search</h3>

        <div>

            <form onSubmit={handleSubmit}>
                <label>
                    pokemon name:
                    <input type="text" name="pokemonName" value={name} />
                </label>

                <br/>

                <label>
                    <input type={"submit"} name={"search"} value={"Search"}/>
                </label>

            </form>

        </div>

        <div>

            <p>pokemon name: {name}</p>
            <p>pokemon weight: {weight}</p>
            <img src={picture} alt={'pokemon sprite'}/>

        </div>
    </>
}
