import {useState} from "react";

export default function(){

    const [number, setNumber] = useState('');
    const [arrayNums, setArrayNums] = useState('');

    function handleChange(event){
        setNumber(event.target.value);
    }

    function handleAdd(){
        const newList = arrayNums.concat( number );
        setArrayNums(newList);
        setNumber('');
    }

   return <div>
       <form>
           <label>
               <p>Number:</p>
               <input type="number" value={number} onChange={handleChange} onClick={handleAdd} />
               <ul>
                    {arrayNums.map(n => <li key = {n} > {n} </li>)}
                </ul>
           </label>
       </form>
   </div>
}
