import {useState} from "react";
import SetColour from "./SetColour";

export default function GetColours(){

    const [colour, setColour] = useState('');

    let handleChange = (element) => {
        button.id
        setColour(element.target.innerText);
    }

    return <div>
        <button id="red" onClick={handleChange}>red</button>
        <button id="blue" onClick={handleChange}>blue</button>
        <button id="green" onClick={handleChange}>green</button>
        {colour && <SetColour color = {colour}/>}
    </div>;
}
